  const agentOutput = $input.first().json.output;
const phoneNumber = $('Extraer Datos WhatsApp').first().json.phone_number;

const isComplete = agentOutput.includes('CADASTRO_COMPLETO');

function mapBusinessType(tipo) {
  const mapping = {
    'restaurante': 'restaurant',
    'padaria': 'cafe',
    'cafeteria': 'cafe',
    'lanchonete': 'cafe',
    'bistro': 'bistro',
    'brasserie': 'brasserie',
    'hotel': 'hotel',
    'catering': 'catering',
    'buffet': 'catering'
  };

  return mapping[tipo.toLowerCase()] || 'restaurant';
}

let parsedData = {
  restaurant_name: null,
  contact_name: null,
  city: null,
  business_type: null
};

if (isComplete) {
  const lines = agentOutput.split('\n');

  for (const line of lines) {
    if (line.includes('Nome Restaurante:')) {
      parsedData.restaurant_name = line.split(':')[1].trim();
    }
    if (line.includes('Nome Contato:')) {
      parsedData.contact_name = line.split(':')[1].trim();
    }
    if (line.includes('Cidade:')) {
      parsedData.city = line.split(':')[1].trim();
    }
    if (line.includes('Tipo:')) {
      const rawType = line.split(':')[1].trim();
      parsedData.business_type = mapBusinessType(rawType);
    }
  }
}

return [{
  json: {
    phone_number: phoneNumber,
    message_to_send: agentOutput,
    onboarding_complete: isComplete,
    collected_data: parsedData
