const userMessage = $input.first().json.message || $input.first().json.user_message;
const phoneNumber = $input.first().json.phone_number;
const sessionId = $input.first().json.session_id;
const restaurantId = $input.first().json.restaurant_id || $('Detectar Opci√≥n del Men√∫').first().json.user_data.restaurant_id;

console.log('üîç [Vector Search] Buscando productos para:', userMessage);

let queryEmbedding;
try {
  const embeddingResponse = await $http.request({
    method: 'POST',
    url: 'https://api.openai.com/v1/embeddings',
    headers: {
      'Authorization': `Bearer ${$credentials.openAiApi.apiKey}`,
      'Content-Type': 'application/json'
    },
    body: {
      model: 'text-embedding-ada-002',
      input: userMessage
    }
  });

  if (embeddingResponse.statusCode !== 200) {
    console.error('‚ùå [Vector Search] OpenAI Embedding error:', embeddingResponse);
    return [{
      json: {
        user_message: userMessage,
        phone_number: phoneNumber,
        session_id: sessionId,
        restaurant_id: restaurantId,
        suggested_products: [],
        products_context: '',
        has_suggestions: false,
        error: 'embedding_generation_failed',
        ...($input.first().json)
      }
    }];
  }

  queryEmbedding = embeddingResponse.data[0].embedding;
  console.log('‚úÖ [Vector Search] Embedding generado exitosamente');

} catch (error) {
  console.error('‚ùå [Vector Search] Error generando embedding:', error);
  return [{
    json: {
      user_message: userMessage,
      phone_number: phoneNumber,
      session_id: sessionId,
      restaurant_id: restaurantId,
      suggested_products: [],
      products_context: '',
      has_suggestions: false,
      error: 'embedding_error',
      ...($input.first().json)
    }
  }];
}

let matchingProducts = [];
try {
  const { data, error } = await $supabase.rpc('match_products_v2', {
    query_embedding: queryEmbedding,
    match_threshold: 0.65,
    match_count: 5
  });

  if (error) {
    console.error('‚ùå [Vector Search] Supabase RPC error:', error.message);
    matchingProducts = [];
  } else {
    matchingProducts = data || [];
    console.log(`‚úÖ [Vector Search] ${matchingProducts.length} productos encontrados`);
  }

} catch (error) {
  console.error('‚ùå [Vector Search] Error en b√∫squeda vectorial:', error);
  matchingProducts = [];
}

let productsContext = '';

if (matchingProducts && matchingProducts.length > 0) {
  productsContext = 'üîç PRODUTOS ENCONTRADOS NO CAT√ÅLOGO:\n\n';
  matchingProducts.forEach((p, idx) => {
    productsContext += `${idx + 1}. ${p.product_name}`;
    if (p.brand) productsContext += ` (${p.brand})`;
    productsContext += '\n';
    productsContext += `   Relev√¢ncia: ${(p.similarity * 100).toFixed(0)}%\n`;

    if (p.alternative_names && Array.isArray(p.alternative_names) && p.alternative_names.length > 0) {
      productsContext += `   Tamb√©m chamado: ${p.alternative_names.join(', ')}\n`;
    }
    productsContext += '\n';
  });
  productsContext += 'üí° Use estes produtos como refer√™ncia.\n';
}

return [{
