      if (line.includes('Fornecedor')) {
        inItems = false;
      }
      if (inItems && line.trim().startsWith('-')) {
        const match = line.match(/- (.+?):\s*([\d.]+)\s*(\w+)/);
        if (match) {
          productos.push({
            produto: match[1].trim(),
            quantidade: parseFloat(match[2]),
            unidade: match[3]
          });
        }
      }
    }

    console.log(`‚úÖ [Detectar Pedido] ${productos.length} produtos detectados`);

    outputLimpio = agentOutput
      .replace(/PEDIDO_COMPLETO/g, '')
      .trim();

  } catch (error) {
