const productos = $input.first().json.produtos;
const restaurantId = $input.first().json.restaurant_id;

console.log(`üîç [Buscar Precios] Buscando precios para ${productos.length} productos`);

// Obtener preferencias del restaurante
const { data: restaurant } = await $supabase
  .from('restaurants')
  .select('price_sensitivity, preferred_suppliers')
  .eq('id', restaurantId)
  .single();

const priceSensitivity = restaurant?.price_sensitivity || 0.5;
const preferredSuppliers = restaurant?.preferred_suppliers || [];

console.log(`üí° [Buscar Precios] Price sensitivity: ${priceSensitivity}`);

const resultados = [];

for (const producto of productos) {
  try {
    // Buscar en master_list por similaridad
    const { data: masterProducts } = await $supabase
      .from('master_list')
      .select('id, product_name')
      .ilike('product_name', `%${producto.produto}%`)
      .limit(3);

    if (!masterProducts || masterProducts.length === 0) {
      console.log(`‚ö†Ô∏è Producto no encontrado en cat√°logo: ${producto.produto}`);
      resultados.push({
        produto: producto.produto,
        quantidade: produto.quantidade,
        unidade: produto.unidade,
        opciones: [],
        no_encontrado: true
      });
      continue;
    }

    const masterListIds = masterProducts.map(p => p.id);

    // Buscar TODOS los precios de TODOS los proveedores
    const { data: supplierPrices } = await $supabase
      .from('supplier_mapped_products')
      .select(\`
        id,
        supplier_id,
        supplier_product_name,
        current_unit_price,
        price_per_unit_type,
        suppliers (
          id,
          company_name,
          is_preferred
        )
      \`)
      .in('master_list_id', masterListIds)
      .eq('is_active', true);

    if (!supplierPrices || supplierPrices.length === 0) {
      console.log(`‚ö†Ô∏è No hay precios para: ${producto.produto}`);
      resultados.push({
        produto: producto.produto,
        quantidade: produto.quantidade,
        unidade: produto.unidade,
        opciones: [],
        sin_precios: true
      });
      continue;
    }

    console.log(`‚úÖ Encontrados ${supplierPrices.length} precios para ${producto.produto}`);

    // Calcular score para cada opci√≥n
    const opciones = supplierPrices.map(price => {
      const supplier = price.suppliers;

      // Score base: precio (invertido y normalizado)
      const allPrices = supplierPrices.map(p => p.current_unit_price);
      const minPrice = Math.min(...allPrices);
      const maxPrice = Math.max(...allPrices);
      const priceRange = maxPrice - minPrice;

      let priceScore = 1.0;
      if (priceRange > 0) {
        priceScore = 1 - ((price.current_unit_price - minPrice) / priceRange);
      }

      // Bonus si es proveedor preferido
      const preferredBonus = preferredSuppliers.includes(supplier.id) ? 0.2 : 0;

      // Score final ponderado por price_sensitivity
      // price_sensitivity alto (0.8) = m√°s importancia a calidad/preferido
      // price_sensitivity bajo (0.2) = m√°s importancia a precio bajo
      const finalScore = (priceScore * (1 - priceSensitivity)) + 
                        (preferredBonus * priceSensitivity);

      return {
        supplier_id: supplier.id,
        supplier_name: supplier.company_name,
        product_name: price.supplier_product_name,
        price: price.current_unit_price,
        unit: price.price_per_unit_type,
        is_preferred: preferredSuppliers.includes(supplier.id),
        price_score: priceScore,
        final_score: finalScore
      };
    });

    // Ordenar por score
    opciones.sort((a, b) => b.final_score - a.final_score);

    resultados.push({
      produto: producto.produto,
      quantidade: produto.quantidade,
      unidade: produto.unidade,
      opciones: opciones,
      mejor_opcion: opciones[0],
      total_opciones: opciones.length
    });

  } catch (error) {
    console.error(`‚ùå Error buscando ${producto.produto}:`, error);
  }
}

