for (const producto of productos) {
  try {
    // Buscar producto en master_list
    const { data: masterProducts } = await $supabase
      .from('master_list')
      .select('id, product_name')
      .ilike('product_name', `%${producto.produto}%`)
      .limit(1);

    if (!masterProducts || masterProducts.length === 0) {
      resultados.push({
        produto: producto.produto,
        problema: 'no_encontrado',
        mensaje: `Produto "${produto.produto}" n√£o encontrado no cat√°logo`
      });
      hayProblemas = true;
      console.log(`‚ö†Ô∏è Produto no encontrado: ${produto.produto}`);
      continue;
    }

    const masterListId = masterProducts[0].id;

    // Verificar precios recientes (< 30 d√≠as)
    const fechaLimite = new Date();
    fechaLimite.setDate(fechaLimite.getDate() - parseInt($('Config Global').first().json.PRICE_VALIDITY_DAYS));

    const { data: recentPrices, error } = await $supabase
      .from('pricing_history')
      .select('id, supplier_id, unit_price, effective_date, suppliers(company_name)')
      .eq('master_list_id', masterListId)
      .gte('effective_date', fechaLimite.toISOString())
      .order('effective_date', { ascending: false })
      .limit(5);

    if (!recentPrices || recentPrices.length === 0) {
      // Buscar el precio m√°s reciente aunque sea antiguo
      const { data: oldestPrice } = await $supabase
        .from('pricing_history')
        .select('effective_date, suppliers(company_name)')
        .eq('master_list_id', masterListId)
        .order('effective_date', { ascending: false })
        .limit(1)
        .single();

      if (oldestPrice) {
        const diasAtras = Math.floor((Date.now() - new Date(oldestPrice.effective_date)) / (1000 * 60 * 60 * 24));
        resultados.push({
          produto: produto.produto,
          problema: 'precos_desatualizados',
          dias_atras: diasAtras,
          ultimo_fornecedor: oldestPrice.suppliers?.company_name,
          mensaje: `Pre√ßos desatualizados (h√° ${diasAtras} dias)`
        });
        hayProblemas = true;
        console.log(`‚ö†Ô∏è Pre√ßos antigos para ${produto.produto}: ${diasAtras} d√≠as`);
