          mensaje: 'Sem pre√ßos cadastrados'
        });
        hayProblemas = true;
        console.log(`‚ö†Ô∏è Sin precios: ${produto.produto}`);
      }
    } else {
      resultados.push({
        produto: produto.produto,
        problema: null,
        precos_encontrados: recentPrices.length,
        mensaje: `‚úÖ ${recentPrices.length} pre√ßos atualizados`
      });
      console.log(`‚úÖ Pre√ßos OK para ${produto.produto}: ${recentPrices.length} opciones`);
    }

  } catch (error) {
    console.error(`‚ùå Error validando ${producto.produto}:`, error);
    resultados.push({
