// üíæ Guardar fornecedor y productos en Supabase
const datos = $input.first().json.dados_fornecedor;
const phoneNumber = $input.first().json.phone_number;

console.log('üíæ [Guardar Fornecedor] Guardando:', datos.nome);

try {
  // 1. Guardar/Actualizar fornecedor
  const { data: existingSupplier } = await $supabase
    .from('suppliers')
    .select('id')
    .ilike('company_name', datos.nome)
    .single();

  let supplierId;

  if (existingSupplier) {
    // Actualizar
    await $supabase
      .from('suppliers')
      .update({
        whatsapp_number: datos.telefone,
        delivery_days: { dias: datos.dias },
        updated_at: new Date().toISOString()
      })
      .eq('id', existingSupplier.id);

    supplierId = existingSupplier.id;
    console.log('‚úÖ Fornecedor actualizado:', supplierId);
  } else {
    // Crear nuevo
    const { data: newSupplier } = await $supabase
      .from('suppliers')
      .insert({
        company_name: datos.nome,
        whatsapp_number: datos.telefone,
        delivery_days: { dias: datos.dias },
        is_active: true,
        created_at: new Date().toISOString()
      })
      .select('id')
      .single();

    supplierId = newSupplier.id;
    console.log('‚úÖ Novo fornecedor criado:', supplierId);
  }

  // 2. Guardar productos del fornecedor
  for (const producto of datos.produtos) {
    // Buscar en master_list
    const { data: masterProduct } = await $supabase
      .from('master_list')
      .select('id')
      .ilike('product_name', `%${producto}%`)
      .limit(1)
      .single();

    const masterListId = masterProduct?.id || null;

    // Crear en supplier_mapped_products
    await $supabase
      .from('supplier_mapped_products')
      .insert({
        supplier_id: supplierId,
        master_list_id: masterListId,
        supplier_product_name: producto,
        current_unit_price: 0,
        currency: 'BRL',
        mapping_confidence: masterListId ? 0.8 : 0.5,
