// This node combines: Mark Session + Update DB + Ask Continuation

// ===== STRUCTURED LOGGING =====
const LOG = {
  prefix: '[Continuation Handler]',
  info: (msg, data) => console.log(`${LOG.prefix} ‚ÑπÔ∏è  ${msg}`, data !== undefined ? JSON.stringify(data).substring(0, 200) : ''),
  success: (msg, data) => console.log(`${LOG.prefix} ‚úÖ ${msg}`, data !== undefined ? JSON.stringify(data).substring(0, 200) : ''),
  error: (msg, err) => console.error(`${LOG.prefix} ‚ùå ${msg}`, err || ''),
  warn: (msg) => console.warn(`${LOG.prefix} ‚ö†Ô∏è  ${msg}`)
};
// ===== END LOGGING =====

try {
  // Validate input
  const input = $input.first();
  if (!input || !input.json) {
    LOG.error('Input vac√≠o o inv√°lido');
    return [{
      json: {
        error: true,
        error_message: 'Input inv√°lido',
        error_node: 'Continuation Handler',
        phone_number: 'unknown'
      }
    }];
  }

  const data = input.json;
  const sessionId = data.session_id;
  const phoneNumber = data.phone_number || data.user_data?.phone_number;

  LOG.info('Processing continuation', { sessionId, phoneNumber });

  // Get continuation message from Config
  const continuationMessage = $('Config Global').first().json.CONTINUATION_MESSAGE;

  // STEP 1: Prepare session update data (will be used by next Supabase node)
  const sessionUpdate = sessionId ? {
    session_id: sessionId,
    session_end: new Date().toISOString(),
    is_completed: true,
    last_activity_at: new Date().toISOString()
  } : null;

  if (!sessionId) {
    LOG.warn('No session_id found, skipping session update');
  } else {
    LOG.success('Session prepared for completion', { sessionId });
  }

  // STEP 2: Return data with continuation message
  const result = {
    ...data,
    output: continuationMessage,
    phone_number: phoneNumber,
    awaiting_continuation: true,
    session_completed: true,
    _session_update: sessionUpdate
  };

  LOG.success('Continuation handler completed');

  return [{ json: result }];

} catch (error) {
  LOG.error(`Error: ${error.message}`);
  LOG.error(`Stack: ${error.stack}`);

  return [{
    json: {
      error: true,
      error_message: error.message,
      error_node: 'Continuation Handler',
      phone_number: input?.json?.phone_number || 'unknown',
      output: 'Desculpe, algo deu errado. Digite "menu" para voltar.'
    }
  }];
}
// ===== END COMBINED CONTINUATION HANDLER =====

// ===== MESSAGE DEDUPLICATION =====
// Previene el env√≠o de mensajes duplicados en un periodo corto de tiempo

const items = $input.all();

if (!items || items.length === 0) {
  console.log('‚ö†Ô∏è [Deduplication] No hay items para procesar');
  return [];
}

const phoneNumber = items[0].json.phone_number;
const output = items[0].json.output || '';

// Crear una key √∫nica basada en phone + primeros 50 caracteres del mensaje
const messageKey = `${phoneNumber}_${output.substring(0, 50)}`;
const now = Date.now();

// Verificar si este mensaje ya se envi√≥ recientemente (√∫ltimos 3 segundos)
const DEDUP_WINDOW_MS = 3000;

// Usar el contexto del workflow para tracking (simulado con timestamp en el mensaje)
const lastSentTimestamp = items[0].json._last_sent_timestamp || 0;

if (output === items[0].json._last_sent_output && (now - lastSentTimestamp) < DEDUP_WINDOW_MS) {
  console.log(`‚ö†Ô∏è [Deduplication] Mensaje duplicado detectado para ${phoneNumber}, omitiendo`);
  console.log(`   Tiempo transcurrido: ${now - lastSentTimestamp}ms`);
  return [];
}

// Agregar timestamp para tracking
items[0].json._last_sent_timestamp = now;
items[0].json._last_sent_output = output;

console.log(`‚úÖ [Deduplication] Mensaje √∫nico confirmado para ${phoneNumber}`);

return items;
